openItems.rest:
border-radius: 50px;
background-color: black;
border: solid 2px white;

border-radius: 20px;
background-color: black;
border: solid 2px white;

font-size:0px;
font-weight: 900;
color: lightskyblue;
@include value-range(1, 1) {
  &::before {
    content: "○";
    font-size: 100px;
  }
  transform: translate(-29px, -10px);
}

@include value-range(2, 2) {
  &::before {
    content: "◎";
    vertical-align: middle;
    font-size: 120px;
  }
    transform: translate(-3px, 0px);
}

font-size: 0px;

@include value-range(1, 1) {
  height: 210px;
  border-style: solid;
  border-color: lightskyblue;
  border-width: 10px;
  background-color: rgba(0,0,0,0);
  z-index: -100;
}

@include value-range(0, 0) {
  display: none;
}
color: black;
background: url('../img/div.png');
background-size: 100px 60px;
background-position: 0px 10px;
background-repeat: no-repeat;

color: rgba(0, 0, 0, 0);
&[value="true"] {
  background: url("../img/chance.png");
  background-size: 100px 50px;
  background-position: 0px 0px;
  background-repeat: no-repeat;
}

color: rgba(0, 0, 0, 0);
&[value="true"] {
  background: url("../img/star.png");
  background-size: 50px 50px;
  background-position: 0px 0px;
  background-repeat: no-repeat;
}

color: rgba(0, 0, 0, 0);
&[value="true"] {
  background: url("../img/pinch.png");
  background-size: 50px 50px;
  background-position: 0px 0px;
  background-repeat: no-repeat;
}

closeItems.rest:
border-radius: 16px;
@include value-range(1, 10) {
  @include bordered;
  background-color: red;
}
@include value-range(11, 300) {
  background-color: black;
  border: solid 2px white;
}
