angular.forEach(players, function(player, index) {
  // pinch, chance
  player.pinch = false;
  player.chance = false;

  // nameLatの設定
  player.nameLat = player.name;
  player.handleNameLat = player.handleName;
  player.keyIndex = 999;

  // rankPoint
  player.rankPoint = player.paperRank * 1.001 + player.rollingRank * 10;

  //course
  if (!angular.isDefined(player.course)) {
    player.course = "";
  }

  // courseWill
  if (player.course === "") {
    player.courseWill =
      player["paperRank"] + "+" +
      player["rollingRank"] + "*10=" +
      (player["paperRank"] + player["rollingRank"] * 10) + " : " +
      player["1"] + player["2"] + player["3"] + player["4"];
  } else {
    player.courseWill = "";
  }
});

var nextPlayer;
var nextPlayers = players.filter(function(p) {
  return p.course === "";
}).sort(function(a, b) {
  return (a.rankPoint < b.rankPoint) ? -1 : 1;
});

if (nextPlayers.length >= 1) {
  nextPlayer = nextPlayers[0];
}

angular.forEach(players, function(player, index) {
  // line
  if (player == nextPlayer) {
    player.next = true;
    player.line = "next";
  } else {
    player.next = false;
    switch (player.course) {
      case "":
        player.line = "left";
        break;
      case "a":
        player.line = "line1";
        break;
      case "b":
        player.line = "line2";
        break;
      case "c":
        player.line = "line3";
        break;
      case "d":
        player.line = "line4";
        break;
      default:
        player.line = "left";
    }
  }
});
